#!/bin/shset -e# Usar el puerto de la variable de entorno PORT o 8080 por defectoPORT=${PORT:-8080}echo "===== Starting Spacebook on port $PORT ====="# Verificar que los archivos estÃ©n en su lugarif [ ! -f "/usr/share/nginx/html/index.html" ]; then    echo "ERROR: index.html not found!"    echo "Contents of /usr/share/nginx/html/:"    ls -la /usr/share/nginx/html/ || echo "Directory does not exist"    exit 1fiecho "Files found in /usr/share/nginx/html/:"ls -la /usr/share/nginx/html/# Crear la configuraciÃ³n de Nginx dinÃ¡micamentecat > /etc/nginx/nginx.conf <<EOFevents {    worker_connections 1024;}http {    include /etc/nginx/mime.types;    default_type application/octet-stream;        server {        listen ${PORT};        server_name localhost;        root /usr/share/nginx/html;        index index.html;                gzip on;        gzip_vary on;        gzip_min_length 1024;        gzip_types text/plain text/css text/xml text/javascript application/x-javascript application/xml+rss application/json;                location ~* \.(jpg|jpeg|png|gif|ico|css|js|svg|woff|woff2|ttf|eot)$ {            expires 1y;            add_header Cache-Control "public, immutable";        }                location / {            try_files \$uri \$uri/ /index.html;        }                add_header X-Frame-Options "SAMEORIGIN" always;        add_header X-Content-Type-Options "nosniff" always;        add_header X-XSS-Protection "1; mode=block" always;    }}EOF# Mostrar la configuraciÃ³n para debugecho "===== Nginx configuration ====="cat /etc/nginx/nginx.confecho "===== End configuration ====="# Probar la configuraciÃ³necho "Testing Nginx configuration..."nginx -t# Iniciar Nginxecho "Starting Nginx..."exec nginx -g "daemon off;"